import React from 'react';
import './App.css';

// Inside React Fragments we can only pass JSX , not React.createElement();

let time ;
const obj = new Date();
let cssObject = {} ;
let hrs = obj.getHours() ;

if(hrs > 6 && hrs <= 12) {
	cssObject.color = 'Green';
	time = 'Good Morning';
}
else if(hrs > 12 && hrs <= 16) {
	cssObject.color = 'Red';
	time = 'Good Afternoon';
}
else if(hrs > 16 && hrs <= 20) {
	cssObject.color = 'Pink';
	time = 'Good Evening';
}
else {
	cssObject.color = 'Cyan';
	time = 'Good Night';
}

function App() {
	return (
		<React.Fragment>			
			<h1 className="greet"> Hello Sir, <span style={ cssObject }> { time } </span> </h1>
		</React.Fragment>
	);
}

export default App;
