import React from 'react';
import './App.css';

// Inside React Fragments we can only pass JSX , not React.createElement();

let time ;
const obj = new Date();

if(obj.getHours() > 6 && obj.getHours() <= 12) {
	time = 'Good Morning';
}
else if(obj.getHours() > 12 && obj.getHours() <= 16) {
	time = ''
}

function App() {
	return (
		<React.Fragment>			
			<h1> Hello Sir, { time } </h1>
		</React.Fragment>
	);
}

export default App;
