import React from 'react';
import './App.css';

// Inside React Fragments we can only pass JSX , not React.createElement();

let time ;
const obj = new Date();
let cssObject = {};

if(obj.getHours() > 6 && obj.getHours() <= 12) {
	cssObject.color = 'Green';
	time = 'Good Morning';
}
else if(obj.getHours() > 12 && obj.getHours() <= 16) {
	cssObject.color = 'Red';
	time = 'Good Afternoon';
}
else if(obj.getHours() > 16 && obj.getHours() <= 20) {
	cssObject.color = 'Pink';
	time = 'Good Evening';
}
else {
	cssObject.color = 'Cyan';
	time = 'Good Night';
}

function App() {
	return (
		<React.Fragment>			
			<h1 className="greet"> Hello Sir, <span style={ cssObject }> { time } </span> </h1>
		</React.Fragment>
	);
}

export default App;
